import React, { useState }from 'react'
import auth0 from '../lib/auth0'

const getMyLocation = () => {
    if(navigator.geolocation){
        navigator.geolocation.getCurrentPosition(position => {
            console.log(position.coords)
        })
    }
}
const CreateStatus = () => {
    return (
        <div>
        <h1>Create Status</h1>
        <label><input type="radio" name="status" value="bem" />Estou bem e sem sintomos</label>
        <label><input type="radio" name="status" value="gripe" />Estou com sintomas de gripe</label>
        <label><input type="radio" name="status" value="covid" />Estoucom sintmas da COVID</label>
        <button onClick={getMyLocation}>Get My Location</button>
        </div>

        )
}

export default CreateStatus

export async function getServerSideProps({ req, res }) {
    const session = await auth0.getSession(req)
    if (session) {
        return {
            props: {
                isAuth: true,
                user: session.user,
            }
        }
    }

    return {
        props: {
            isAuth: false,
            user: {}

        }

    } 

}
