import React, { useState }from 'react'
import auth0 from '../lib/auth0'

const CreateStatus = () => {
    const [dados, setDados] = useState({
        status: 'bem',
        coords: {
            lat: null,
            long: null
        }
    })

    const getMyLocation = () => {
        if(navigator.geolocation){
            navigator.geolocation.getCurrentPosition(position => {

                setDados(old => {
                    return {
                        ...old,
                        coords: {
                            lat: position.coords.latitude,
                            long: position.coords.longitude
                        }
                    }
                })
            })
        }
    }
    const onStatusChange = evt => {
        console.log(evt.target.value) 
    }
    return (
        <div>
        <h1>Create Status</h1>
        <label className="block"><input type="radio" name="status" value="bem" onClick={onStatusChange} />Estou bem e sem sintomos</label>
        <label className="block"><input type="radio" name="status" value="gripe" onClick={onStatusChange} />Estou com sintomas de gripe / resfriado</label>
        <label className="block"><input type="radio" name="status" value="covid" onClick={onStatusChange} />Estoucom sintmas da COVID</label>
        Sua posição atual: {JSON.stringify(dados)}
        <button onClick={getMyLocation}>Get My Location</button>
        </div>

        )
}

export default CreateStatus

export async function getServerSideProps({ req, res }) {
    const session = await auth0.getSession(req)
    if (session) {
        return {
            props: {
                isAuth: true,
                user: session.user,
            }
        }
    }

    return {
        props: {
            isAuth: false,
            user: {}

        }

    } 

}
